	public String getCoverNoteNo(String policyNo) {
		String covernote_No = "";
		if (policyNo != null && !policyNo.equals("")) {
			String pol[] = policyNo.split("/");

			for (int i = 0; i < pol.length; i++) {
				if (pol[i] != null && pol[i].length() > 6) {
					covernote_No = pol[i];
					break;
				}
			}
		}
		return covernote_No;
	}

